let setActiveLink=()=>{let t=window.location.pathname.split("/").pop()||"index.html";document.querySelectorAll(".nav__link").forEach(e=>{e.getAttribute("href").replace("./","")===t?e.classList.add("active"):e.classList.remove("active")})};function setupMobileMenu(){let t=document.querySelector(".mobile-menu"),n=document.querySelector(".menu-toggle");t&&n&&(n.addEventListener("click",e=>{e.stopPropagation(),t.classList.toggle("open")}),t.addEventListener("click",e=>{"A"===e.target.tagName&&t.classList.remove("open")}),document.addEventListener("click",e=>{t.contains(e.target)||n.contains(e.target)||t.classList.remove("open")}),setActiveLink())}document.addEventListener("DOMContentLoaded",()=>{setupMobileMenu()});