class GameHandler{constructor(){this.iframe=null,this.fullscreenBtn=null,this.startBtn=null,this.init()}init(){this.iframe=document.getElementById("game-iframe"),this.fullscreenBtn=document.getElementById("fullscreen-btn"),this.startBtn=document.getElementById("start-game-btn"),this.setupEventListeners()}setupEventListeners(){this.fullscreenBtn&&this.fullscreenBtn.addEventListener("click",()=>this.toggleFullscreen()),this.startBtn&&this.startBtn.addEventListener("click",()=>this.startGame())}toggleFullscreen(){this.iframe&&(document.fullscreenElement?document.exitFullscreen():this.iframe.requestFullscreen().catch(t=>{}))}startGame(){this.iframe&&(this.iframe.style.display="block",this.iframe.focus())}}document.addEventListener("DOMContentLoaded",()=>{new GameHandler});